var portfolioView={};portfolioView.initialize=function(){$("#aboutMe").hide(),$("#contact").hide()},portfolioView.navViews=function(){$(".main-nav").on("click",function(i){i.preventDefault(),"About"==i.target.text&&($("section > div").slideUp(300),$("#aboutMe").delay(200).hide().slideDown(300)),"Work"==i.target.text&&($("section > div").slideUp(300),$("#workTemplate").delay(200).hide().each(function(i){$(this).delay(200*i).slideDown(300)})),"Contact"==i.target.text&&($("section > div").slideUp(300),$("#contact").animate({height:"toggle"},300))})},$(document).ready(function(){portfolioView.initialize(),portfolioView.navViews()});